/// <reference path="imagine-gulp-tools.d.ts" />

let gulp = require("gulp");
let util = require("gulp-util");  
let transform = require("gulp-json-transform");

/** copies files & folders from a source glob to a destination  */
copyJson = (options) => {
    return gulp.src(options.src)
        .pipe(options.transform ? transform(options.transform) : util.noop())
        .pipe(gulp.dest(options.dest));
}

module.exports = copyJson;