/**
 * Imagine LogEnhancer 1.2
 */
declare module Imagine.LogEnhancer {
    const enum LogLevel {
        All = 0,
        Debug = 1,
        Info = 2,
        Log = 3,
        Warn = 4,
        Error = 5,
    }
    interface ILoggerCallbackFunction {
        (args: any[], className: string, now: number): void;
    }
    interface ILoggerCallback {
        level: LogLevel;
        callback: Function;
    }
    interface IEnhancedLogger extends angular.ILogService {
        getInstance: (className: string, showInit?: boolean) => IEnhancedLogger;
        registerCallback: (callback: ILoggerCallbackFunction, level: LogLevel) => void;
        isEnhanced: boolean;
        debugBasic: angular.ILogCall;
        infoBasic: angular.ILogCall;
        logBasic: angular.ILogCall;
        warnBasic: angular.ILogCall;
        errorBasic: angular.ILogCall;
        formatMessage: (time: number, className: string, args: any[]) => string;
    }
}
